import{_ as o,c as n,o as d,ae as s}from"./chunks/framework.CCkVsPTE.js";const u=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"adrs/006-use-pnpm-as-node-build-tool.md","filePath":"adrs/006-use-pnpm-as-node-build-tool.md"}'),t={name:"adrs/006-use-pnpm-as-node-build-tool.md"};function c(i,e,a,p,r,l){return d(),n("div",null,e[0]||(e[0]=[s('<h2 id="adr-006-use-pnpm-as-node-build-tool-for-docs" tabindex="-1">ADR-006: Use pnpm as Node Build Tool for Docs <a class="header-anchor" href="#adr-006-use-pnpm-as-node-build-tool-for-docs" aria-label="Permalink to “ADR-006: Use pnpm as Node Build Tool for Docs”">​</a></h2><p><strong>Decision</strong>: Use <code>pnpm</code> instead of <code>npm</code> or <code>yarn</code> to manage documentation site dependencies and scripts.</p><p><strong>Status</strong>: Accepted</p><p><strong>Context</strong>: The documentation site is built using VitePress, a Vue-powered static site generator. To install and manage its dependencies, a Node-based package manager is required. <code>pnpm</code> offers performance and consistency improvements over <code>npm</code> and <code>yarn</code>, including disk-saving symlinks, strict dependency resolution, and reproducible builds.</p><p><strong>Decision</strong>:</p><ul><li>Use <code>pnpm</code> to manage all frontend dependencies in the <code>/docs</code> directory</li><li>Use local <code>pnpm</code> scripts for <code>docs:dev</code>, <code>docs:build</code>, and <code>docs:serve</code></li><li>Document usage in developer setup instructions</li></ul><p><strong>Consequences</strong>:</p><ul><li>Developers need to install <code>pnpm</code> (1-time setup)</li><li>Builds are faster and more reliable</li><li>Avoids dependency duplication in CI environments</li></ul><p><strong>Advice</strong>: Include <code>pnpm-lock.yaml</code> and <code>.npmrc</code> in version control. Add documentation scripts to <code>package.json</code>. Recommend installing <code>pnpm</code> globally with <code>npm install -g pnpm</code>. Update CI pipelines (e.g., GitHub Actions) to use <code>pnpm install</code> instead of <code>npm ci</code>.</p>',9)]))}const g=o(t,[["render",c]]);export{u as __pageData,g as default};
